const S11059 = require('@chirimen/s11059');
const { promisify } = require('util');
const sleep = promisify(setTimeout);

main();

async function main() {
  const i2cAccess = await requestI2CAccess();
  const port = i2cAccess.ports.get(1);
  const s11059 = new S11059(port, 0x2a);
